export default class PokeService {

    _apiBase = 'https://pokeapi.co/api/v2';

    async getResource(url) {
        const res = await fetch(`${this._apiBase}${url}`);

        if (!res.ok) {
            throw new Error(`Could not fetch ${url}` +
                `, received ${res.status}`)
        }
        return await res.json();
    }

    async getAllPokemons() {
        const res = await this.getResource(`/pokemon/`);
        return res.results;
    }

    async getPokemon(id) {
        const pokemon = await this.getResource(`/pokemon/${id}/`);
        return this._transformPokemon(pokemon);
    }

    async getAllBerries() {
        const res = await this.getResource(`/berry/`);
        return res.results.map(this._transformPokemon());
    }

    getBerry(id) {
        return this.getResource(`/berry/${id}/`);
    }

    async getAllItems() {
        const res = await this.getResource(`/item/`);
        return res.results;
    }

    getItem(id) {
        return this.getResource(`/item/${id}/`);
    }

    _transformPokemon(pokemon) {

        const idRegExp = /\/([0-9]*)\/$/;
        const id = pokemon.url.match(idRegExp)[1];

        return {

            id,

            name: pokemon.name,
            base_experience: pokemon.base_experience,
            height: pokemon.height,
            weight: pokemon.weight

        }
    }
}
