import React, {Component} from 'react';

import '../../services/poke-service';

import './random-pokemon.css';
import PokeService from "../../services/poke-service";
import Spinner from'../spinner/spinner';

export default class RandomPokemon extends Component {

    pokeService = new PokeService();

    state = {

        pokemon:{},
        loading: true

    };

    constructor() {
        super();
        this.updatePokemon();
    }


    onPokemonLoaded = (pokemon)=>{

        this.setState({pokemon,loading: false});




    };

    updatePokemon() {
        const id = Math.floor(Math.random()*100+2);
        this.pokeService.getPokemon(id)
            .then(this.onPokemonLoaded);


    }


    render() {



        const {pokemon:{name, base_experience, height, weight},loading} = this.state;

        if (loading){
            return<Spinner />;
        }

        return (
            <div className="random-planet jumbotron rounded ">
                <img className="planet-image"
                     src={`https://img.pokemondb.net/artwork/large/${name}.jpg`}/>
                <div>
                    <h4>Pokemon name</h4>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item">
                            <span className="term">Pokemon name</span>
                            <span>{name}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Base Experience</span>
                            <span>{base_experience}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Height</span>
                            <span>{height}</span>
                        </li>

                        <li className="list-group-item">
                            <span className="term">Weight</span>
                            <span>{weight}</span>
                        </li>

                    </ul>
                </div>
            </div>

        );
    }
}

const PokemonView = () => {
    return
}
