import React, {Component} from 'react';

import '../../services/poke-service'

import './random-pokemon.css';
import PokeService from "../../services/poke-service";

export default class RandomPokemon extends Component {

    state = {
        name:null,

        species:null,
        height:null,
        weight:null

    };

    updatePokemon(){
        this.PokeService.getPokemon(1).then((pokemon)=>{

            this.setState({

                name:pokemon.name,
                species:pokemon.species,
                forms:pokemon.forms,
                height:pokemon.height,
                weight:pokemon.weight


            });

        });
    }

    render() {

        const {name,species,height,weight,forms} = this.state;
        return (
            <div className="random-planet jumbotron rounded">
                <img className="planet-image"
                     src="https://starwars-visualguide.com/assets/img/planets/5.jpg"/>
                <div>
                    <h4>Pokemon name</h4>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item">
                            <span className="term"></span>
                            <span>{forms}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Species</span>
                            <span>Seed Pok√©mon</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Height</span>
                            <span>0.7 m </span>
                        </li>

                        <li className="list-group-item">
                            <span className="term">Weight</span>
                            <span>6.9 kg</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Abilities</span>
                            <span>Overgrow</span>
                        </li>
                    </ul>
                </div>
            </div>

        );
    }
}
